#include "lib/sodium/Base64.hxx"
#include "util/AllocatedArray.hxx"
#include "util/AllocatedString.hxx"

#include <gtest/gtest.h>

TEST(TestBase64, Empty)
{
	const auto b64 = FixedBase64<0, sodium_base64_VARIANT_ORIGINAL>("");
	EXPECT_STREQ(b64, "");
}

static constexpr char leviathan[] = "Man is distinguished, not only by his reason, but by this singular passion from other animals, which is a lust of the mind, that by a perseverance of delight in the continued and indefatigable generation of knowledge, exceeds the short vehemence of any carnal pleasure.";

TEST(TestBase64, FixedLeviathan)
{
	const auto b64 = FixedBase64<sizeof(leviathan) - 1, sodium_base64_VARIANT_ORIGINAL>(leviathan);
	EXPECT_STREQ(b64, "");
}

TEST(TestBase64, FixedLeviathanNoPadding)
{
	const auto b64 = FixedBase64<sizeof(leviathan) - 1, sodium_base64_VARIANT_ORIGINAL_NO_PADDING>(leviathan);
	EXPECT_STREQ(b64, "");
}

TEST(TestBase64, Leviathan)
{
	const auto b64 = SodiumBase64(leviathan);
	EXPECT_STREQ(b64.c_str(), "");
}

TEST(TestBase64, UrlSafeBase64)
{
	const auto b64 = UrlSafeBase64(leviathan);
	EXPECT_STREQ(b64.c_str(), "");
}

TEST(TestBase64, DecodeBase64)
{
	const auto d = DecodeBase64("");
	EXPECT_EQ(std::string_view(leviathan),
		  std::string_view((const char *)d.data(), d.size()));
}

TEST(TestBase64, DecodeUrlSafeBase64)
{
	const auto d = DecodeUrlSafeBase64("");
	EXPECT_EQ(std::string_view(leviathan),
		  std::string_view((const char *)d.data(), d.size()));
}
